// import e from "express";
import "./styles/style.css";

function WeatherHome(props) {
  const {
    // weather,
    search,
    // setSearch,
    // SearchWeatherLocation,
    currentLocation,
    setCurrentLocation,
    // currentCity,
    setCurrentCity,
  } = props;

  function handleSubmit(event) {
    event.preventDefault();
    setCurrentCity(currentLocation);
  }

  console.log(search);

  return (
    <header>
      <div>
        <div className="search-box">
          <h1> The Mr.Weather App </h1>
        </div>
        <div className="search-box">
          <h2>Today's Weather</h2>
        </div>
        <div className="container">
          <form onSubmit={handleSubmit}>
            <div className="search-box">
              <input
                type="text"
                placeholder="Find Your City"
                className="search-bar"
                onChange={(searchEvent) =>
                  setCurrentLocation(searchEvent.target.value)
                }
                value={currentLocation}
                // onKeyPress={SearchWeatherLocation(search)}
              />
            </div>
            <button type="submit">
              <span>SUBMIT</span>
            </button>
          </form>
        </div>
        <div className="container">
          <ul>
            {/* {search == Array ? (
              search.map((weather, index) => {
                <li key={index}>
                  <div>{weather.name}</div>
                  <div>{weather.sys.country}</div>
                </li>;
              })
            ) : (
              // : console.log("the data in search is not an array")
              <li>{search.message}</li>
            )} */}
            <li>city: {search.name}</li>
            <li>Max Temperature: {search.main.temp_max}</li>
          </ul>
        </div>
      </div>
    </header>
  );
}

export default WeatherHome;
